import { GenericObject, SaffronOptions } from "../etc/types";
/**
 * Saffron command builder.
 *
 * Type Parameters:
 *
 * A = Shape of the application's parsed arguments.
 *
 * C = Shape of the application's parsed configuration file, which by default
 *     has the same shape as A.
 */
export default function buildCommand<A extends GenericObject = any, C extends GenericObject = A>(options: SaffronOptions<A, C>): void;
